pragma solidity =0.8.15;

import "@openzeppelin/contracts/utils/math/Math.sol";

library OddsLib {
    // Precision to be used in calculations
    uint256 public constant PRECISION = 1e6;

    /*
        * @dev Adjust the odds downwards linearly to 1
        * @param wager The amount wagered
        * @param pool The total amount in the pool
        * @return The adjusted decimal odds given
    */
    function getLinearAdjustedOdds(
        uint256 wager,
        uint256 odds,
        uint256 liquidity
    ) external pure returns (uint256) {
        assert(odds >= 1 * PRECISION);
        uint256 unadjustedPayout = odds * wager / PRECISION;

        // odds * unadjustedPayout / (liquidity + wager)
		uint256 oddsAdjustment = Math.mulDiv(odds, unadjustedPayout, liquidity + wager, Math.Rounding.Down);
        // If we have gone past the floor, clip to 1
        if (oddsAdjustment > odds) {
            return 1 * PRECISION;
        }
        return Math.max(1 * PRECISION, odds - oddsAdjustment);
    }

    /*
        * @dev Reduces odds on a curve that approaches 0 as the payout increases, such that the payout will always be less then free liquidity. Increase the coefficient to make the curve steeper. 2 is a good starting point.
        * @param wager The amount of the wager
        * @param odds The decimals odds of the wager, expressed as a number
        * @return The new adjusted decimal odds
    */
    function getCurvedAdjustedOdds(
        uint256 wager,
        uint256 odds,
        uint256 liquidity
    ) external pure returns (uint256) {
        assert(odds >= 1 * PRECISION);
        uint256 SQRT_PRECISION = 1e3;
        uint256 potentialPayout = (wager * odds / PRECISION);
        uint256 adjustedPayout = (liquidity + wager) -
            (liquidity * SQRT_PRECISION) /
            Math.sqrt(
                2 * (potentialPayout * PRECISION) / liquidity + (1 * PRECISION),
                Math.Rounding.Up
            );
        // Return the odds need to generate this adjusted payout with the given wager
        return Math.max(1 * PRECISION, (adjustedPayout * PRECISION) / wager);
    }

    // Assuming that the market previously had targetMargin, then after some runners have been scratched, correct the odds of this runner to make up the margin again
    function rebaseOddsWithScratch(uint256 odds, uint256 scratchedOdds, uint256 targetMargin) external pure returns (uint256) {
        assert(odds > 0);
        uint256 newMargin = targetMargin - PRECISION / scratchedOdds;
        return changeMargin(odds, newMargin, targetMargin);
    } 

    function changeMargin(uint256 odds, uint256 margin, uint256 targetMargin) public pure returns (uint256) {
        return addMargin(removeMargin(odds, margin), targetMargin);
    }

    function removeMargin(uint256 odds, uint256 margin) public pure returns (uint256) {
        return odds * margin / PRECISION;
    }

    function addMargin(uint256 odds, uint256 margin) public pure returns (uint256) {
        assert(margin > 0);
        return odds * PRECISION / margin;
    }

    // Given an array of odds, return the margin
    function getMargin(uint256[] calldata odds) public pure returns (uint256) {
        uint256 total; // 0
        uint256 oddsCount = odds.length;
        for (uint256 i = 0; i < oddsCount; i++) {
            if (odds[i] == 0) continue;
            total += ((PRECISION * PRECISION) / odds[i]);
        }
        return total;
    }
}","keccak256":"0x83508dd30664d07c984ef69dcf1ea0618137c04e59237996e56ca9c98265a59f","license":"MIT"},"contracts/SignatureLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.15;

library SignatureLib {
    struct Signature {
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    function recoverSigner(
        bytes32 message,
        Signature memory signature
    ) public pure returns (address) {
        bytes32 prefixedHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", message)
        );
        return ecrecover(prefixedHash, signature.v, signature.r, signature.s);
    }
}
","keccak256":"0x3d2aea16b52e98b22e10addc4401c67151de809a94688cc5fa7c0e5529b65ccb","license":"MIT"},"contracts/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.15;
import "./ERC4626Metadata.sol";
import "./Market.sol";
import "@openzeppelin/contracts/token/ERC20/presets/ERC20PresetMinterPauser.sol";
import "@openzeppelin/contracts/interfaces/IERC20Metadata.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Vault is ERC4626Metadata, Ownable {

    using Math for uint256;
    using SafeERC20 for IERC20;

    // These will change to allow multiple markets
    address private _market;
    address private _self;

    constructor(IERC20Metadata asset_)
    ERC4626Metadata(
        asset_
    ) 
    ERC20(
        string(abi.encodePacked("HL ", asset_.name())),
        string(abi.encodePacked("HL", asset_.symbol()))
    ) {
        require(
            address(asset_) != address(0),
            "Underlying address is invalid"
        );
        _self = address(this);
    }

    // Override decimals to be the same as the underlying asset
    /*function decimals() public view override returns (uint8) {
        return _decimals;
    }*/

    function setMarket(address market, uint256 max) public onlyOwner {
        require(_market == address(0), "setMarket: Market already set");
        _market = market;
        IERC20(asset()).approve(_market, max);
    }

    function getMarket() external view returns (address) {
        return _market;
    }

    function getPerformance() external view returns (uint256) {
        return _getPerformance();
    }

    function _getPerformance() private view returns (uint256) {
        uint256 underlyingBalance = totalAssets();
        if (underlyingBalance > 0)
            return (totalSupply() * 100) / underlyingBalance;

        return 0;
    }

    // If receiver is omitted, use the sender
    function deposit(uint256 assets, address receiver) public override returns (uint256) {
        if (receiver == address(0)) receiver = _msgSender();
        return super.deposit(assets, receiver);
    }

    function getMarketAllowance() external view withMarket returns (uint256) {
        // TODO: This will change to allow multiple markets, using msg.sender
        uint256 allowance = ERC20(asset()).allowance(_self, _market);
        if (allowance > totalAssets()) {
            return totalAssets();
        }

        return allowance;
    }

    // Total Assets = amount held by the vault, plus amount lent to the market and therefore locked
    function totalAssets() public view override returns (uint256) {
        return IERC20(asset()).balanceOf(_self);
    }

    function totalAssetsLocked() external view returns (uint256) {
        return IMarket(_market).getTotalExposure();
    }

    /** @dev See {IERC4626-maxRedeem}. */
    function maxRedeem(address owner) public view override returns (uint256) {
        if (totalAssets() == 0) return 0;

        // A fraction of the total assets is locked is locked for the owner, proportional to their holdings.
        uint256 shareBalance = balanceOf(owner);
        uint256 sharesLockedForOwner = shareBalance / totalAssets();
        return shareBalance - sharesLockedForOwner;
    }

    modifier onlyMarket() {
        require(_market != address(0), "onlyMarket: Market not set");
        require(
            msg.sender == _market,
            "onlyMarket: Only the market can call this function"
        );
        _;
    }

    modifier withMarket() {
        require(
            _market != address(0),
            "deposit: Not allowed until market is set"
        );
        _;
    }
}
","keccak256":"0x3bb11099ab2524e134ff2810210845c37507e6143a9e43522a4eed7e0b3290c5","license":"MIT"},"contracts/VaultTimeLock.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.15;
import "./Vault.sol";

contract VaultTimeLock is Vault {

    // Mapping address => uint256
    // Unix time until receiver can withdraw from vault
    mapping(address => uint256) public lockedTime;

    // Duration of lock up period in seconds
    uint256 public immutable lockDuration;

    constructor(IERC20Metadata asset_, uint256 lockDuration_)
    Vault(
        asset_
    ) 
    {
        lockDuration = lockDuration_;
    }

    // Override _deposit function to require owner to be receiver
    // Set lock time on owner to block transfers until after lock period

    /**
     * @dev Deposit/mint common workflow.
     */
    function _deposit(
        address caller,
        address receiver,
        uint256 assets,
        uint256 shares
    ) internal virtual override {
        lockedTime[receiver] = block.timestamp + lockDuration;
        return super._deposit(caller, receiver, assets, shares);
    }

    /**
     * @dev Withdraw/redeem common workflow.
     */
    function _withdraw(
        address caller,
        address receiver,
        address owner,
        uint256 assets,
        uint256 shares
    ) internal virtual override {
        require(lockedTime[owner] < block.timestamp, "_withdraw: Locked time not passed");
        return super._withdraw(caller, receiver, owner, assets, shares);
    }

    // transfer/transferFrom common workflow.
    // Override _transfer function to require lock time to be passed
    // This is what prevents users bypassing withdrawals before lock period is over
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(
            lockedTime[from] < block.timestamp,
            "_transfer: Locked time not passed"
        );
        return super._transfer(from, to, amount);
    }
}
